site.headStyle:
@media (max-width: 480px) {
  body.silex-runtime.is-not-active .show-when-active {
    display: none;
  }

  body.silex-runtime.is-active .show-when-not-active {
    display: none;
  }
}

@media (min-width: 480px) {
  .close-btn,
  .open-btn {
    cursor: pointer;
  }

  body.silex-runtime .show-when-active {
    bottom: 100%;
    top: unset;
    position: absolute !important;
  }

  html {
    background: linear-gradient(to left, #0fdb80, #7fefbd);
    min-width: 1000px;
    height: 100%;
  }
}

html {
  background: linear-gradient(to left, #0fdb80, #7fefbd);
  height: 100%;
}

body.silex-runtime {
  transition: .2s all ease-out;
}

body.is-active.silex-runtime>.section-element {
  margin-bottom: -1px;
}

body.is-active.silex-runtime {
  box-shadow: 0 -2px 10px 8px rgba(0, 0, 0, 0.11);
}

/*
body.is-active .show-when-not-active {
   height: 0; 
}
*/

@media (min-width: 480px) {
  body.is-active.silex-runtime {
    transform: translateY(100px);
  }
}

@media (min-width: 1000px) {
  body.is-active.silex-runtime {
    transform: scale(0.9, 0.9) translateY(100px);
  }
}

@media (min-width: 480px) {
  .silex-runtime .thumb > * {
    opacity: 0;
    transition: opacity .5s;
  }
  .silex-runtime .thumb:hover > * {
    opacity: 1;
  }
}



/* Menu Button CSS */
.btn {
  width: 150px;
  height: 50px;
  border-radius: 5px;
  border: none;
  transition: all 0.5s ease-in-out;
  font-size: 20px;
  font-family: Verdana, Geneva, Tahoma, sans-serif;
  font-weight: 600;
  display: flex;
  align-items: center;
  background: #040f16;
  color: #f5f5f5;
}

.btn:hover {
  box-shadow: 0 0 20px 0px #2e2e2e3a;
}

.btn .icon {
  position: absolute;
  height: 40px;
  width: 70px;
  display: flex;
  justify-content: center;
  align-items: center;
  transition: all 0.5s;
}

.btn .text {
  transform: translateX(55px);
}

.btn:hover .icon {
  width: 175px;
}

.btn:hover .text {
  transition: all 0.5s;
  opacity: 0;
}

.btn:focus {
  outline: none;
}

.btn:active .icon {
  transform: scale(0.85);
}



/* Category Button CSS */
.cat-btn {
  font-family: Courgette, sans-serif;
  text-decoration: none;
  padding: 20px 40px;
  font-size: 1.8em;
  position: relative;
  margin: 32px;
  border: none;
  background-color: #26caf8;
  font-weight: 600;
}

.cat-btn-bead {
  font-family: Courgette, sans-serif;
  text-decoration: none;
  padding: 20px 40px;
  font-size: 1.8em;
  position: relative;
  margin: 32px;
  border: none;
  background-color: #ba2ffa;
  font-weight: 600;
}

.cat-btn-crochet {
  font-family: Courgette, sans-serif;
  text-decoration: none;
  padding: 20px 40px;
  font-size: 1.8em;
  position: relative;
  margin: 32px;
  border: none;
  background-color: #fa2ff0;
  font-weight: 600;
}

.cat-btn-painting {
  font-family: Courgette, sans-serif;
  text-decoration: none;
  padding: 20px 40px;
  font-size: 1.8em;
  position: relative;
  margin: 32px;
  border: none;
  background-color: #2ffa8e;
  font-weight: 600;
}

.cat-btn-1 {
  overflow: hidden;
  color: #fff;
  border-radius: 15px;
  box-shadow: 0 0 0 0 rgba(143, 64, 248, 0.5), 0 0 0 0 rgba(39, 200, 255, 0.5);
  transition: transform 0.3s ease, box-shadow 0.3s ease;
}

.cat-btn-1::after {
  content: "";
  width: 500px;
  height: 500px;
  position: absolute;
  top: -50px;
  left: -100px;
  background-color: #ff3cac;
  background-image: linear-gradient(225deg, #27d86c 0%, #26caf8 50%, #c625d0 100%);
  z-index: -1;
  transition: transform 0.5s ease;
}

.btn-bead::after {
  content: "";
  width: 500px;
  height: 500px;
  position: absolute;
  top: -50px;
  left: -100px;
  background-color: #ff3cac;
  background-image: linear-gradient(225deg, #27d86c 0%, #c841d7 50%, #c625d0 100%);
  z-index: -1;
  transition: transform 0.5s ease;
}

.cat-btn-1:hover {
  transform: translate(0, -6px);
  box-shadow: 10px -10px 25px 0 rgba(143, 64, 248, 0.5),  -10px 10px 25px 0 rgba(39, 200, 255, 0.5);
}

.cat-btn-1:hover::after {
  transform: rotate(150deg);
}

/* Rainbow Button */

.btn-rainbow .main {
  display: flex;
  align-items: center;
  justify-content: center;
}

.btn-rainbow {
  font-family: Courgette, sans-serif;
  width: 170px;
  height: 60px;
  font-size: 23px;
  background: #ffe5fd;
  border-radius: 50px;
  color: #000;
  outline: none;
  cursor: pointer;
  transition: all 0.4s;
}

.btn-rainbow:hover {
  box-shadow: inset 0 0 0 4px #ef476f, 
              inset 0 0 0 8px #ffd166, 
              inset 0 0 0 12px #06d6a0,
              inset 0 0 0 16px #118ab2;
  background: #073b4c;
  color: #fff;
}

site.headScript:
$(function() {
    /* hamburger menu */
    $('.close-btn, .open-btn').click(function() {
        $('body').toggleClass('is-not-active is-active');
    });

    /*
        animation widget from here: https://github.com/silexlabs/Silex/issues/443
    */

    window.sr = ScrollReveal({
        distance: '100px'
    });
    sr.reveal('.from-left', { origin:  'left'});
    sr.reveal('.from-right', { origin:  'right'});
    sr.reveal('.from-top', { origin:  'top'});
    sr.reveal('.from-bottom', { origin:  'bottom'});

})
