site.headStyle:
@media (max-width: 480px) {
  body.silex-runtime.is-not-active .show-when-active {
    display: none;
  }

  body.silex-runtime.is-active .show-when-not-active {
    display: none;
  }
}

@media (min-width: 480px) {
  .close-btn,
  .open-btn {
    cursor: pointer;
  }

  body.silex-runtime .show-when-active {
    bottom: 100%;
    top: unset;
    position: absolute !important;
  }

  html {
    background: linear-gradient(to left, #0fdb80, #7fefbd);
    min-width: 1000px;
    height: 100%;
  }
}

html {
  background: linear-gradient(to left, #0fdb80, #7fefbd);
  height: 100%;
}

body.silex-runtime {
  transition: .2s all ease-out;
}

body.is-active.silex-runtime>.section-element {
  margin-bottom: -1px;
}

body.is-active.silex-runtime {
  box-shadow: 0 -2px 10px 8px rgba(0, 0, 0, 0.11);
}

/*
body.is-active .show-when-not-active {
   height: 0; 
}
*/

@media (min-width: 480px) {
  body.is-active.silex-runtime {
    transform: translateY(100px);
  }
}

@media (min-width: 1000px) {
  body.is-active.silex-runtime {
    transform: scale(0.9, 0.9) translateY(100px);
  }
}

@media (min-width: 480px) {
  .silex-runtime .thumb > * {
    opacity: 0;
    transition: opacity .5s;
  }
  .silex-runtime .thumb:hover > * {
    opacity: 1;
  }
}



/* Menu Button CSS */
.btn {
  width: 150px;
  height: 50px;
  border-radius: 5px;
  border: none;
  transition: all 0.5s ease-in-out;
  font-size: 20px;
  font-family: Verdana, Geneva, Tahoma, sans-serif;
  font-weight: 600;
  display: flex;
  align-items: center;
  background: #040f16;
  color: #f5f5f5;
}

.btn:hover {
  box-shadow: 0 0 20px 0px #2e2e2e3a;
}

.btn .icon {
  position: absolute;
  height: 40px;
  width: 70px;
  display: flex;
  justify-content: center;
  align-items: center;
  transition: all 0.5s;
}

.btn .text {
  transform: translateX(55px);
}

.btn:hover .icon {
  width: 175px;
}

.btn:hover .text {
  transition: all 0.5s;
  opacity: 0;
}

.btn:focus {
  outline: none;
}

.btn:active .icon {
  transform: scale(0.85);
}



/* Category Button CSS */
.cat-btn {
  font-family: Courgette, sans-serif;
  text-decoration: none;
  padding: 20px 40px;
  font-size: 1.8em;
  position: relative;
  margin: 32px;
  border: none;
  background-color: #26caf8;
  background-image: linear-gradient(225deg, #26caf8 50%, #256cd0 100%);
  font-weight: 600;
}

.cat-btn-bead {
  font-family: Courgette, sans-serif;
  text-decoration: none;
  padding: 20px 40px;
  font-size: 1.8em;
  position: relative;
  margin: 32px;
  border: none;
  background-color: #ba2ffa;
  background-image: linear-gradient(225deg, #ba2ffa 50%, #7f1bad 100%);
  font-weight: 600;
}

.cat-btn-crochet {
  font-family: Courgette, sans-serif;
  text-decoration: none;
  padding: 20px 40px;
  font-size: 1.8em;
  position: relative;
  margin: 32px;
  border: none;
  background-color: #fa2ff0;
  background-image: linear-gradient(225deg, #fa2ff0 50%, #fa2f9f 100%);
  font-weight: 600;
}

.cat-btn-painting {
  font-family: Courgette, sans-serif;
  text-decoration: none;
  padding: 37px 57px 40px 57px;
  font-size: 1.8em;
  position: relative;
  margin: 32px;
  border: none;
  background-color: #2ffa8e;
  background-image: linear-gradient(225deg, #2ffa8e 50%, #17c869 100%);
  font-weight: 600;
}

.cat-btn-1 {
  overflow: hidden;
  color: #fff;
  border-radius: 15px;
  box-shadow: 0 0 0 0 rgba(143, 64, 248, 0.5), 0 0 0 0 rgba(39, 200, 255, 0.5);
  transition: transform 0.3s ease, box-shadow 0.3s ease;
}

.cat-btn-1::after {
  content: "";
  width: 500px;
  height: 500px;
  position: absolute;
  top: -50px;
  left: -100px;
  background-color: #ff3cac;
  background-image: linear-gradient(225deg, #27d86c 0%, #26caf8 50%, #c625d0 100%);
  z-index: -1;
  transition: transform 0.5s ease;
}

.btn-bead::after {
  content: "";
  width: 500px;
  height: 500px;
  position: absolute;
  top: -50px;
  left: -100px;
  background-color: #ff3cac;
  background-image: linear-gradient(225deg, #ff03ea 0%, #ba2ffa 50%, #6506ad 100%);
  z-index: -1;
  transition: transform 0.5s ease;
}

.btn-crot::after {
  content: "";
  width: 500px;
  height: 500px;
  position: absolute;
  top: -50px;
  left: -100px;
  background-color: #ff3cac;
  background-image: linear-gradient(225deg, #e89cff 0%, #fa2ff0 50%, #aa0d47 100%);
  z-index: -1;
  transition: transform 0.5s ease;
}

.btn-paint::after {
  content: "";
  width: 500px;
  height: 500px;
  position: absolute;
  top: -50px;
  left: -100px;
  background-color: #ff3cac;
  background-image: linear-gradient(225deg, #c4ff20 0%, #2ffa8e 50%, #0096dc 100%);
  z-index: -1;
  transition: transform 0.5s ease;
}

.cat-btn-1:hover {
  transform: translate(0, -6px);
  box-shadow: 10px -10px 25px 0 rgba(143, 64, 248, 0.5),  -10px 10px 25px 0 rgba(39, 200, 255, 0.5);
}

.cat-btn-1:hover::after {
  transform: rotate(150deg);
}

/* Rainbow Button */

.btn-rainbow .main {
  display: flex;
  align-items: center;
  justify-content: center;
}

.btn-rainbow {
  font-family: Courgette, sans-serif;
  width: 170px;
  height: 60px;
  font-size: 23px;
  background: #ffe5fd;
  border-radius: 50px;
  color: #000;
  outline: none;
  cursor: pointer;
  transition: all 0.4s;
}

.btn-rainbow:hover {
  box-shadow: inset 0 0 0 4px #ef476f, 
              inset 0 0 0 8px #ffd166, 
              inset 0 0 0 12px #06d6a0,
              inset 0 0 0 16px #118ab2;
  background: #073b4c;
  color: #fff;
}


/*  Fancy Mobile Buttons  */
.btn-mobile {
  display: flex;
  justify-content: center;
  align-items: center;
  width: 20rem;
  overflow: hidden;
  height: 15rem;
  background-size: 300% 300%;
  backdrop-filter: blur(1rem);
  border-radius: 5rem;
  transition: 0.5s;
  animation: gradient_301 5s ease infinite;
  border: double 8px transparent;
  background-image: linear-gradient(#212121, #212121),  linear-gradient(137.48deg, #ffdb3b 10%,#FE53BB 45%, #8F51EA 67%, #0044ff 87%);
  background-origin: border-box;
  background-clip: content-box, border-box;
}

.btn-star {
  display: flex;
  justify-content: center;
  align-items: center;
  width: 15rem;
  overflow: hidden;
  height: 8rem;
  background-size: 300% 300%;
  backdrop-filter: blur(1rem);
  border-radius: 2rem;
  transition: 0.5s;
  animation: gradient_301 5s ease infinite;
  border: double 8px transparent;
  background-image: linear-gradient(#212121, #212121),  linear-gradient(137.48deg, #ffdb3b 10%,#FE53BB 45%, #8F51EA 67%, #0044ff 87%);
  background-origin: border-box;
  background-clip: content-box, border-box;
}

#container-stars {
  position: absolute;
  z-index: -1;
  width: 100%;
  height: 100%;
  overflow: hidden;
  transition: 0.5s;
  backdrop-filter: blur(1rem);
  border-radius: 5rem;
}

#container-stars2 {
  position: absolute;
  z-index: -1;
  width: 100%;
  height: 100%;
  overflow: hidden;
  transition: 0.5s;
  backdrop-filter: blur(1rem);
  border-radius: 2rem;
}

strong {
  z-index: 2;
  font-family: Courgette, sans-serif;
  font-size: 25px;
  letter-spacing: 5px;
  color: #FFFFFF;
  text-shadow: 0 0 4px white;
}

.pp {
  z-index: 2;
  font-family: Courgette, sans-serif;
  font-size: 25px;
  letter-spacing: 5px;
  color: #0a0a0a;
  text-shadow: 0 0 4px white;
}

.strong2 {
  z-index: 2;
  font-family: Courgette, sans-serif;
  font-size: 35px;
  letter-spacing: 5px;
  color: #FFFFFF;
  text-shadow: 0 0 4px white;
}

#glow {
  position: absolute;
  display: flex;
  width: 12rem;
}

.circle {
  width: 100%;
  height: 30px;
  filter: blur(2rem);
  animation: pulse_3011 4s infinite;
  z-index: -1;
}

.circle:nth-of-type(1) {
  background: rgba(254, 83, 186, 0.636);
}

.circle:nth-of-type(2) {
  background: rgba(142, 81, 234, 0.704);
}

.btn:hover #container-stars {
  z-index: 1;
  background-color: #212121;
}

.btn:hover {
  transform: scale(1.1)
}

.btn:active {
  border: double 4px #FE53BB;
  background-origin: border-box;
  background-clip: content-box, border-box;
  animation: none;
}

.btn:active .circle {
  background: #FE53BB;
}

#stars {
  position: relative;
  background: transparent;
  width: 200rem;
  height: 200rem;
}

#stars::after {
  content: "";
  position: absolute;
  top: -10rem;
  left: -100rem;
  width: 100%;
  height: 100%;
  animation: animStarRotate 90s linear infinite;
}

#stars::after {
  background-image: radial-gradient(#ffffff 1px, transparent 1%);
  background-size: 50px 50px;
}

#stars::before {
  content: "";
  position: absolute;
  top: 0;
  left: -50%;
  width: 170%;
  height: 500%;
  animation: animStar 60s linear infinite;
}

#stars::before {
  background-image: radial-gradient(#ffffff 1px, transparent 1%);
  background-size: 50px 50px;
  opacity: 0.5;
}

@keyframes animStar {
  from {
    transform: translateY(0);
  }

  to {
    transform: translateY(-135rem);
  }
}

@keyframes animStarRotate {
  from {
    transform: rotate(360deg);
  }

  to {
    transform: rotate(0);
  }
}

@keyframes gradient_301 {
  0% {
    background-position: 0% 50%;
  }

  50% {
    background-position: 100% 50%;
  }

  100% {
    background-position: 0% 50%;
  }
}

@keyframes pulse_3011 {
  0% {
    transform: scale(0.75);
    box-shadow: 0 0 0 0 rgba(0, 0, 0, 0.7);
  }

  70% {
    transform: scale(1);
    box-shadow: 0 0 0 10px rgba(0, 0, 0, 0);
  }

  100% {
    transform: scale(0.75);
    box-shadow: 0 0 0 0 rgba(0, 0, 0, 0);
  }
}

/* animated hover image */
.grow-img{
transition: 0.5s ease;
}

.grow-img:hover{
-webkit-transform: scale(1.1);
-ms-transform: scale(1.1);
transform: scale(1.1);
transition: 0.5s ease;
}

/* Google Play Button */
.playstore-button {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  border: 4px solid #000;
  border-radius: 9999px;
  background-color: rgba(0, 0, 0, 1);
  padding: 1.25rem 3rem;
  text-align: center;
  color: rgba(255, 255, 255, 1);
  outline: 0;
  transition: all  .2s ease;
  text-decoration: none;
}

.playstore-button:hover {
  background-color: transparent;
  color: rgba(0, 0, 0, 1);
}

.icon {
  height: 3rem;
  width: 3rem;
}

.texts {
  margin-left: 2rem;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  line-height: 1.5;
}

.text-1 {
  margin-bottom: 1rem;
  font-size: 1rem;
  line-height: 1.5rem;
}

.text-2 {
  font-weight: 2000;
  font-size: 1.5rem;
}

site.headScript:
$(function() {
    /* hamburger menu */
    $('.close-btn, .open-btn').click(function() {
        $('body').toggleClass('is-not-active is-active');
    });

    /*
        animation widget from here: https://github.com/silexlabs/Silex/issues/443
    */

    window.sr = ScrollReveal({
        distance: '100px'
    });
    sr.reveal('.from-left', { origin:  'left'});
    sr.reveal('.from-right', { origin:  'right'});
    sr.reveal('.from-top', { origin:  'top'});
    sr.reveal('.from-bottom', { origin:  'bottom'});

})
